const fs=require("fs"),path=require("path"),inlineCssPath=path.join(__dirname,"../build/inline.css"),appHtmlPath=path.join(__dirname,"../src/app.html"),outputAppHtmlPath=path.join(__dirname,"../build/app.html");function inlineCss(){try{const css=fs.readFileSync(inlineCssPath,"utf8"),inlined=fs.readFileSync(appHtmlPath,"utf8").replace("</head>",`<style>${css}</style></head>`);fs.writeFileSync(outputAppHtmlPath,inlined),console.log("✅ Critical CSS inlined successfully")}catch(error){console.error("❌ Error inlining CSS:",error)}}inlineCss();